# coding=utf-8

from sqlalchemy import create_engine
from sqlalchemy.orm import sessionmaker
from models import Base, User, Book
from datetime import datetime, timedelta
from random import randint

engine = create_engine('sqlite:///catalog.db')
Base.metadata.bind = engine

DBSession = sessionmaker(bind=engine)
session = DBSession()

def randomDate():
    now = datetime.now()
    interval = randint(0, 259200)
    date_added = now - timedelta(seconds = interval)
    return date_added

if __name__ == '__main__':
    session.query(User).delete()
    session.commit()

    session.query(Book).delete()
    session.commit()

    franny = User(name='Franny Glass', email='franny@glassfamily.com', picture='franny.jpg', username='franny-glass',location_lat=-33.811901,location_lng=151.003446,city=u'Paramatta, NSW',country='Australia')
    zooey = User(name='Zooey Glass', email='zooey@glassfamily.com', picture='zooey.jpg', username='zooey-glass',location_lat=-34.065034,location_lng=151.014986,city=u'Engadine, NSW',country='Australia')
    walt = User(name='Walt Glass', email='walt@glassfamily.com', picture='walt.jpg', username='walt-glass',location_lat=-33.965103,location_lng=151.135810,city=u'Kogarah, Sydney',country='Australia')
    waker = User(name='Waker Glass', email='waker@glassfamily.com', picture='waker.jpg', username='waker-glass',location_lat=-33.986912,location_lng=151.232899,city=u'La Perouse, Sydney',country='Australia')
    seymour = User(name='Seymour Glass', email='seymour@glassfamily.com', picture='seymour.jpg', username='seymour-glass',location_lat=-33.959358,location_lng=151.098833,city=u'Hurstville, Sydney',country='Australia')
    booboo = User(name='Beatrice Tannenbaum', email='booboo@glassfamily.com', picture='booboo.jpg', username='beatrice-tannenbaum',location_lat=-33.900471,location_lng=151.173289,city=u'Newtown, Sydney',country='Australia')
    buddy = User(name='Buddy Glass', email='buddy@glassfamily.com', picture='buddy.jpg', username='buddy-glass',location_lat=-33.883170,location_lng=151.200544,city=u'Ultimo, Sydney',country='Australia')
    andrea = User(name='Andrea Crawford', email='andrea.crawford1d3@gmail.com', picture='booboo.jpg', username='andrea-crawford',location_lat=-33.858260,location_lng=151.180336,city=u'Balmain, Sydney',country='Australia')

    session.add(franny)
